import React, { useState } from 'react';
import axios from 'axios';

const CreateComplaint = ({ loggedIn, userData }) => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [address, setAddress] = useState('');
  const [message, setMessage] = useState('');
  
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!loggedIn) {
      setMessage('Giriş yapmadan arıza kaydı oluşturamazsınız.');
     
     return;
    }
    try {
      // Kullanıcının ID'sini kullanarak sunucuya arıza kaydı oluşturun
      console.log(user_id,'benim idim'); 
      const response = await axios.post('http://localhost:5000/complaint', {
      
        user_id: user_id,
        title,
        description,
        address,
      });
      
      setMessage(response.data.message);
    } catch (error) {
      console.error(error);
      setMessage('Arıza kaydı oluşturulamadı.');
    }
  };

  return (
    <div className='centerdiv'>
      <h1>Arıza Oluştur(Foto ekleme eklenmeli)</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label className='altbaslik'>Başlık:</label><br/>
          <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
        </div>
        <div>
          <label className='altbaslik'>Açıklama:</label><br/>
          <textarea value={description} onChange={(e) => setDescription(e.target.value)} />
        </div>
        <div>
          <label className='altbaslik'>Açık Adres:</label><br/>
          <input type="text" value={address} onChange={(e) => setAddress(e.target.value)} />
        </div>
        <button className='button buton' type="submit">Kayıt Oluştur</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default CreateComplaint;
