import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [questionText, setQuestionText] = useState('');
  const [questionType, setQuestionType] = useState('');
  const [difficulty, setDifficulty] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const createQuestion = async () => {
    setIsLoading(true);
    try {
      const response = await axios.post('http://localhost:5000/create_question', {
        text: questionText,
        type: questionType,
        difficulty: difficulty,
      });

      console.log(response.data);

      // Başarılı mesajları işleyebilirsiniz
    } catch (error) {
      console.error('Hata oluştu:', error);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="App">
      <h1>Soru Oluşturma Uygulaması</h1>
      <div>
        <label>Soru Metni:</label>
        <input type="text" onChange={(e) => setQuestionText(e.target.value)} />
      </div>
      <div>
        <label>Soru Türü:</label>
        <input type="text" onChange={(e) => setQuestionType(e.target.value)} />
      </div>
      <div>
        <label>Zorluk Derecesi:</label>
        <input type="text" onChange={(e) => setDifficulty(e.target.value)} />
      </div>
      <button onClick={createQuestion} disabled={isLoading}>
        {isLoading ? 'Soru Oluşturuluyor...' : 'Soru Oluştur'}
      </button>
    </div>
  );
}

export default App;
