import React, { useState } from 'react';
import axios from 'axios';

const CreateComplaint = ({ loggedIn, user_id }) => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [address, setAddress] = useState('');
  const [image, setImage] = useState(null); // Resmi burada tutacağız
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!loggedIn) {
      setMessage('Giriş yapmadan arıza kaydı oluşturamazsınız.');
      return;
    }

    try {
      const formData = new FormData();
      formData.append('user_id', user_id);
      formData.append('title', title);
      formData.append('description', description);
      formData.append('address', address);
      formData.append('image', image); // Resmi FormData'ya ekle

      const response = await axios.post('http://localhost:5000/complaint', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }, // FormData kullandığınızda header'ı ayarlayın
      });

      setMessage(response.data.message);
    } catch (error) {
      console.error(error);
      setMessage('Arıza kaydı oluşturulamadı.');
    }
  };

  return (
    <div className='centerdiv'>
      <h1>Arıza Oluştur</h1>
      <form onSubmit={handleSubmit}>
        {/* Diğer inputlar */}
        <div>
          <label className='altbaslik'>Resim Yükle:</label><br/>
          <input type="file" accept="image/*" onChange={(e) => setImage(e.target.files[0])} />
        </div>
        {/* Diğer inputlar */}
        <button className='button buton' type="submit">Kayıt Oluştur</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default CreateComplaint;
