import React, { useState, useEffect } from 'react';
import { OpenAI } from 'openai';
import axios from 'axios';
import { createRoot } from 'react-dom/client';

function App() {
  const [generatedQuestion, setGeneratedQuestion] = useState('');
  const [userInput, setUserInput] = useState({
    soru: '',
    dogru_cevap: '',
    yanlis_cevap1: '',
    yanlis_cevap2: '',
    yanlis_cevap3: '',
    zorluk_seviyesi: '',
  });

  const apiKey = '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';

  const api = new OpenAI({
    apiKey,
    dangerouslyAllowBrowser: true,
  });

  const createAndSendQuestion = async () => {
    const soru =
      'Bana sadece 1 adet soru üret. sorunun konuları tarih, coğrafya, bilim, mantık matematik ve futbol ile alakalı olsun.soruları olabildiğince zor sor. temel sorular olmasın.zorluk seviyesini belirtirken bilinirliğe göre 1 ila 5 arasında puanla bu şekilde yazmanı isitiyorum senden soruyu iki düz çizgi arasına yaz |Soru| |Doğru cevap| |Yanlış cevap 1| |Yanlış cevap 2| |Yanlış cevap 3| |Tür| |Zorluk seviyesi| örnek verecek olursam |Kösedağ savaşaı hangi iki devlet arasında olmuştur| |Anadolu SELÇUKLU-MOĞOL| |GAZNELİ-SELÇUKLU| |Azerbaycan -ermenistan| |nijerya-bangladeş| |tARİH | |2|';

    try {
      const completion = await api.chat.completions.create({
        messages: [{ role: 'system', content: soru }, { role: 'user', content: '' }],
        model: 'gpt-3.5-turbo',
      });

      const aiResponse = completion.choices[0].message.content;
      console.log(`AI: ${aiResponse}`);

      const veriler = aiResponse.split('|');
      const soruMetni = veriler[1];
      const dogruCevap = veriler[3];
      const yanlisCevap1 = veriler[5];
      const yanlisCevap2 = veriler[7];
      const yanlisCevap3 = veriler[9];
      const tur = veriler[11];
      const zorlukSeviyesi = veriler[13];

      setGeneratedQuestion(aiResponse);

      setUserInput({
        soru: soruMetni,
        dogru_cevap: dogruCevap,
        yanlis_cevap1: yanlisCevap1,
        yanlis_cevap2: yanlisCevap2,
        yanlis_cevap3: yanlisCevap3,
        zorluk_seviyesi: zorlukSeviyesi,
      });

      return aiResponse;
    } catch (error) {
      console.error('Hata oluştu:', error);
      return '';
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserInput({ ...userInput, [name]: value });
  };

  const yeniSoruOlustur = () => {
    setUserInput({
      soru: '',
      dogru_cevap: '',
      yanlis_cevap1: '',
      yanlis_cevap2: '',
      yanlis_cevap3: '',
      zorluk_seviyesi: '',
    });
    setGeneratedQuestion('');
  };

  useEffect(() => {
    createAndSendQuestion();
  }, []);

  const kaydet = async () => {
    const { soru, dogru_cevap, yanlis_cevap1, yanlis_cevap2, yanlis_cevap3, zorluk_seviyesi } = userInput;

    try {
      const formData = new FormData();

      formData.append('soru', soru);
      formData.append('dogru_cevap', dogru_cevap);
      formData.append('yanlis_cevap1', yanlis_cevap1);
      formData.append('yanlis_cevap2', yanlis_cevap2);
      formData.append('yanlis_cevap3', yanlis_cevap3);
      formData.append('zorluk_seviyesi', zorluk_seviyesi);

      const response1 = await axios.post('http://localhost:5000/create_question', formData, {
        headers: {
          'Content-Type': 'application/json',
        },
      });

      console.log(response1.data);

      createAndSendQuestion();
    } catch (error) {
      console.error('Hata oluştu:', error);
    }
  };

  return (
    <div className="App">
      <h1>Soru Oluşturma Uygulaması</h1>
      <label>
        Soru:
        <input type="text" name="soru" value={userInput.soru} onChange={handleInputChange} />
      </label>
      <label>
        Doğru Cevap:
        <input type="text" name="dogru_cevap" value={userInput.dogru_cevap} onChange={handleInputChange} />
      </label>
      <label>
        Yanlış Cevap 1:
        <input type="text" name="yanlis_cevap1" value={userInput.yanlis_cevap1} onChange={handleInputChange} />
      </label>
      <label>
        Yanlış Cevap 2:
        <input type="text" name="yanlis_cevap2" value={userInput.yanlis_cevap2} onChange={handleInputChange} />
      </label>
      <label>
        Yanlış Cevap 3:
        <input type="text" name="yanlis_cevap3" value={userInput.yanlis_cevap3} onChange={handleInputChange} />
      </label>
      <label>
        Zorluk Seviyesi:
        <input
          type="number"
          name="zorluk_seviyesi"
          value={userInput.zorluk_seviyesi}
          onChange={handleInputChange}
        />
      </label>
      <button onClick={kaydet}>Soruyu Kaydet</button>
      <button onClick={yeniSoruOlustur}>Yeni Soru Oluştur</button>
      {generatedQuestion && (
        <div>
          <h2>Oluşturulan Soru:</h2>
          <p>{generatedQuestion}</p>
        </div>
      )}
    </div>
  );
}

const root = createRoot(document.getElementById('root'));

root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

export default App;
