import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import Home from './components/Home';
import LoginPage from './components/LoginPage';
import RegisterPage from './components/RegisterPage';
import AdminPanel from './components/AdminPanel';
import OpenComplaints from './components/OpenComplaints';
import SolvedComplaints from './components/SolvedComplaints';
import UserComplaints from './components/UserComplaints';
import CreateComplaint from './components/CreateComplaint';
import ComplaintDetail from './components/ComplaintDetail';
import Navbar from './components/Navbar';
import Iletisim from './components/Iletisim';
import AdminSurveys from './components/AdminSurveys';
import './styles.css';
import "bootstrap/dist/css/bootstrap.min.css"
const App = () => {
  const [loggedIn, setLoggedIn] = useState(false);
  const [userData, setUserData] = useState({
    username: '',
    user_id: '',
    is_admin: '',
    is_yetkili: ''
  });

  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      setLoggedIn(true);
      setUserData(JSON.parse(storedUser));
    }
  }, []);

  const handleLogin = (userData) => {
    setLoggedIn(true);
    setUserData(userData);
    localStorage.setItem('user', JSON.stringify(userData));
  };

  
  
  
  
  
  return (
    <Router>
      <Navbar />
     
     
        
        <div className='arkaplan'></div>

        <Routes>
          
          <Route path="/" element={<Home loggedIn={loggedIn} username={username} />} />
          <Route path="/register" element={<RegisterPage />} />
          <Route path="/login" element={<LoginPage handleLogin={handleLogin} />} />
          <Route path="/admin" element={<AdminPanel user_id={user_id} />} />
          <Route path="/open-complaints" element={<OpenComplaints is_admin={is_admin} is_yetkili={is_yetkili} />} />
          <Route path="/solved-complaints" element={<SolvedComplaints is_admin={is_admin} is_yetkili={is_yetkili} />} />
          <Route path="/user-complaints" element={<UserComplaints user_id={user_id} />} />
          <Route path="/create-complaint" element={<CreateComplaint loggedIn={loggedIn} user_id={user_id} />} />
          <Route path="/complaint/:complaint_id" element={<ComplaintDetail  is_yetkili={is_yetkili}  />} />
          <Route path="/Iletisim" element={<Iletisim />} />
          <Route path="/AdminSurveys" element={<AdminSurveys/>} />
        </Routes>
      
    </Router>
  );
};

export default App;
