import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const LoginPage = ({ handleLogin }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLoginSubmit = async () => {
    try {
      const response = await axios.post('http://localhost:5000/login', { username, password });
      const message = response.data.message;
      
      if (message === 'Giriş başarılı') {
        setError('');
        
        const { user_id, is_admin, is_yetkili, token } = response.data;
        handleLogin({ user_id, is_admin, is_yetkili });
        localStorage.setItem('token', token); // Token'ı localStorage'a kaydet
        
        if (is_admin === false && is_yetkili === false)
          navigate('/user-complaints');
        if (is_admin === true && is_yetkili === false)
          navigate('/admin');
        if (is_yetkili)
          navigate('/');
      } else {
        setError('Kullanıcı adı veya şifre hatalı');
      }
    } catch (error) {
      console.log(error);
      setError('Bir hata oluştu, lütfen tekrar deneyin.');
    }
  };

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      // Tarayıcıda token varsa otomatik giriş yap
      // Token'ı sunucuya gönderip geçerliliğini kontrol etme işlemini yapabilirsiniz
    }
  }, []);

  return (
    <div className='centerdiv'>
      <input
        type="text"
        placeholder="Kullanıcı Adı"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <br />
      <br />
      <input
        type="password"
        placeholder="Şifre"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <br />
      <button className='buton button' onClick={handleLoginSubmit}>
        Giriş Yap
      </button>
      {error && <div>{error}</div>}
    </div>
  );
};

export default LoginPage;
