import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [generatedQuestion, setGeneratedQuestion] = useState('');

  // createAndSendQuestion işlemini bir kez çağırmak için useEffect kullanabilirsiniz.
  useEffect(() => {
    createAndSendQuestion();
  }, []);

  const createAndSendQuestion = async () => {
    try {
      // OpenAI API'sini kullanarak soru oluşturun
      const response = await axios.post('/generate_question');
      const generatedQuestion = response.data.question;

      // Oluşturulan soruyu kullanıcı arayüzünde gösterin
      setGeneratedQuestion(generatedQuestion);
    } catch (error) {
      console.error('Hata oluştu:', error);
    }
  };

  return (
    <div className="App">
      <h1>Soru Oluşturma Uygulaması</h1>
      <button onClick={createAndSendQuestion}>Soru Oluştur</button>
      {generatedQuestion && (
        <div>
          <h2>Oluşturulan Soru:</h2>
          <p>{generatedQuestion}</p>
        </div>
      )}
    </div>
  );
}

export default App;
