import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [questionText, setQuestionText] = useState('');
  const [questionType, setQuestionType] = useState('');
  const [difficulty, setDifficulty] = useState('');
  const [generatedQuestion, setGeneratedQuestion] = useState('');

  const createQuestion = async () => {
    try {
      const response = await axios.post('/generate_question', {
        soru: questionText,
        tur: questionType,
        zorluk_seviyesi: difficulty,
      });

      const generatedQuestion = response.data.message;
      setGeneratedQuestion(generatedQuestion);

      // Başarılı mesajları işleyebilirsiniz
    } catch (error) {
      console.error('Hata oluştu:', error);
    }
  };

  return (
    <div className="App">
      <h1>Soru Oluşturma Uygulaması</h1>
      <div>
        <label>Soru Metni:</label>
        <input type="text" onChange={(e) => setQuestionText(e.target.value)} />
      </div>
      <div>
        <label>Soru Türü:</label>
        <input type="text" onChange={(e) => setQuestionType(e.target.value)} />
      </div>
      <div>
        <label>Zorluk Derecesi (1-5 arası):</label>
        <input type="text" onChange={(e) => setDifficulty(e.target.value)} />
      </div>
      <button onClick={createQuestion}>Soru Oluştur</button>
      {generatedQuestion && (
        <div>
          <h2>Oluşturulan Soru:</h2>
          <p>{generatedQuestion}</p>
        </div>
      )}
    </div>
  );
}

export default App;
