import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const LoginPage = ({ handleLogin }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
 
  const [error, setError] = useState('');
  
 
  const navigate = useNavigate();
  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      const { user_id, is_admin, is_yetkili } = JSON.parse(storedUser);
      handleLogin({ user_id, is_admin, is_yetkili });
      if (is_admin && is_yetkili) {
        navigate('/');
      } else if (is_admin) {
        navigate('/admin');
      } else {
        navigate('/user-complaints');
      }
    }
  }, [handleLogin, navigate]);
  const handleLoginSubmit = async () => {
    try {
      const response = await axios.post('http://localhost:5000/login', { username, password });
    const user_id=response.data.user_id;
    const is_admin=response.data.is_admin;
    const is_yetkili=response.data.is_yetkili;
    
    const message = response.data.message;
      if (message === 'Giriş başarılı') {
        setError('');
        handleLogin({ username,user_id,is_admin,is_yetkili}); // handleLogin prop'unu kullanarak giriş yaptığımızı bildiriyoruz.
        const user = { user_id, is_admin, is_yetkili };
        localStorage.setItem('user', JSON.stringify(user));
        console.log(user_id,'userid',is_admin,is_yetkili);
        if(is_admin===false&&is_yetkili===false)
        navigate('/user-complaints');
        if(is_admin===true&&is_yetkili===false)
        navigate('/admin');
        if(is_yetkili)
        navigate('/');
     
     
      } else {
        setError('Kullanıcı adı veya şifre hatalı');
      }
    } catch (error) {
      console.log(error);
      setError('Bir hata oluştu, lütfen tekrar deneyin.');
    }
  };

  return (
    <div className='centerdiv'>
      <input
        type="text"
        placeholder="Kullanıcı Adı"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
        
      />
      <br />
      <br />
      <input
        type="password"
        placeholder="Şifre"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        
      />
      <br />
      <button className='buton button' onClick={handleLoginSubmit} >
        Giriş Yap
      </button>
      {error && <div>{error}</div>}
    </div>
  );
};

export default LoginPage;
